import styled from "styled-components";

export const Div = styled.Div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 250px;
  width: 100%;
  margin: 0px 15px;
  font-size: 4em;

  & button {
    border: none;
  }
`;
