import styled from "styled-components";

export default styled.div`
  display: flex;
  gap: 5%;
  justify-content: center;
  align-items: center;
  height: 250px;
  width: 100%;
  margin: 0px 15px;
  font-size: 4em;
  background-color: green;
`;
